import t8 from './assets/images/t8.png';
import t9 from './assets/images/t9.png';
import t10 from './assets/images/t10.png';
import t11 from './assets/images/t11.png';

const imgUrl = [t1, t2, t3, t4, t5, t6, t7, t8, t9, t10, t11];

// preload images
imgUrl.forEach((url) => {
